<?php	session_start();	if(isset($_SESSION['admin']))	{		echo		'<p align="center" style="font-size: x-large;"><br /><strong>User Registration Information</strong></p>		<form action="showData.php" method="post">			<table cellspacing="10" cellpadding="10" style="background: whitesmoke;" align="center">			<tr>				<td style="font-size: large;">Select Department : </td>				<td>					<select name="selectdepartment" id="d" style="width: 200px;height: 25px;font-size: large;">						<option>All</option>						<option>CSE</option>						<option>ECE</option>						<option>EE</option>						<option>CE</option>					</select>				</td>			</tr>			<tr>				<td></td>				<td align="right"><input type="submit" value="SHOW USER COUNT" name="count"></td>			</tr>			<tr>				<td></td>				<td align="right"><input type="submit" value="SHOW USER DATA" name="data"></td>			</tr>			</table>		</form>		';				$link = mysqli_connect('localhost', 'root', 'root');		mysqli_set_charset($link, 'utf8');		mysqli_select_db($link, 'scrapbook2012');				if(!empty($_REQUEST['count']))		{			if($_REQUEST['selectdepartment']=='All')			{$sql = 'SELECT COUNT(*) AS COUNT FROM register;';}			else			{$sql = 'SELECT COUNT(*) AS COUNT FROM register where department="'.$_REQUEST['selectdepartment'].'";';}			$result = mysqli_query($link, $sql);			while($row = mysqli_fetch_array($result))			{				echo				'<p style="font-size: x-large;" align="center"><br />				The total number of users registered from 				<strong>'.$_REQUEST['selectdepartment'].'</strong>				departments = <strong>'.$row['COUNT'].'</strong>				</p>';			}		}		else if(!empty($_REQUEST['data']))		{			if($_REQUEST['selectdepartment']=='All')			{$sql = 'SELECT name,username FROM register;';}			else			{$sql = 'SELECT name,username FROM register where department="'.$_REQUEST['selectdepartment'].'";';}			$result = mysqli_query($link, $sql);			echo				'<p style="font-size: x-large;" align="center"><br />				The total list of users registered from 				<strong>'.$_REQUEST['selectdepartment'].'</strong>				departments are -<br /><br />				<table cellspacing="10" cellpadding="10" style="background: wheat;" align="center">				<thead>				<tr>					<th>NAME</th>					<th>USERNAME</th>				</tr>				</thead>				<tbody>';			while($row = mysqli_fetch_array($result))			{				echo				'<tr align="center">				<td>'.$row['name'].'</td>				<td>'.$row['username'].'</td>				</tr>';			}			echo				'</tbody></table>				<br />				<a href="/../myProjects/scrapbook2012/logout.php">LOGOUT FROM ADMIN PAGE</a>				</p>';		}	}	else	{		header('Location: /../scrapbook2012');		exit();	}	?>